from autoplex.benchmark.phonons.utils import get_rmse


def test_rmse(test_dir, clean_dir):
    import os
    from monty.serialization import loadfn
    from atomate2.common.schemas.phonons import PhononBSDOSDoc

    # test with two different band-structures

    dft_data = loadfn(test_dir / "benchmark" / "PhononBSDOSDoc_LiCl.json")
    ml_data = loadfn(test_dir / "benchmark" / "PhononBSDOSMLDoc_LiCl.json")
    dft_doc: PhononBSDOSDoc = dft_data["output"]
    ml_doc: PhononBSDOSDoc = ml_data["output"]

    os.chdir(test_dir / "benchmark")

    rmse = get_rmse(ml_bs=ml_doc.phonon_bandstructure, dft_bs=dft_doc.phonon_bandstructure)
    rmse_qdep = get_rmse(ml_bs=ml_doc.phonon_bandstructure, dft_bs=dft_doc.phonon_bandstructure, q_dependent_rmse=True)

