import pytest

from pycnpj_cpf.core import cnpj_or_cpf_is_valid


@pytest.mark.unit
@pytest.mark.high
@pytest.mark.parametrize(
    'value',
    [
        '492.711.290-08',
        '299.621.190-14',
        '205.936.010-25',
        '41821790057',
        '34445321052',
        '17168587048',
        '33.378.832/0001-03',
        '81.109.827/0001-30',
        '85.840.881/0001-01',
        '11.222.333/0001-81',
        '72.101.517/0001-88',
        '37.453.242/0001-40',
        '69408076000157',
        '42278632000161',
        'AA.AAA.AAA/AAAA-45',
    ],
)
def test_positive_cnpj_or_cpf_is_valid(value):
    """Test positive for function cpf_or_cnpj_is_valid."""
    assert cnpj_or_cpf_is_valid(value)


